import type { DisplayObjectConfig } from '../../types';
type CustomElement = string | HTMLElement;
export type TooltipPosition = 'top' | 'bottom' | 'left' | 'right' | 'top-left' | 'top-right' | 'bottom-left' | 'bottom-right';
export type TooltipItem = {
    name?: string;
    value?: number | string;
    index?: number;
    color?: string;
    [key: string]: any;
};
export interface TooltipCfg {
    x?: number;
    y?: number;
    visibility?: 'visible' | 'hidden';
    /** 标题 */
    title?: string;
    position?: TooltipPosition;
    offset?: [number, number];
    autoPosition?: boolean;
    /** 指针是否可进入 */
    enterable?: boolean;
    /** 画布的左上角坐标 */
    container: {
        x: number;
        y: number;
    };
    /** tooltip 在画布中的边界 */
    bounding: {
        x: number;
        y: number;
        width: number;
        height: number;
    };
    items?: TooltipItem[];
    template?: {
        container?: string;
        title?: string;
        item?: string;
    };
    customContent?: CustomElement;
    /**
     * 样式
     */
    style?: {
        [key: string]: {
            [key: string]: any;
        };
    };
}
export type TooltipOptions = DisplayObjectConfig<TooltipCfg>;
export {};
